        ref={containerRef}
        className="relative w-full max-w-[1260px] aspect-[1260/477] cursor-ew-resize select-none overflow-hidden group"
        onMouseDown={onMouseDown}
        onTouchStart={onTouchStart}
        onClick={(e) => handleMove(e.clientX)}
